import React, { useEffect, useState } from "react";

const messages = [
  "Zdravo! ðŸ‘‹",
  "Ja sam tvoj AI asistent.",
  "Tu sam da odgovorim na pitanja korisnika.",
  "Pogledaj demo ili me poveÅ¾i na svoj sajt!",
];

const ChatbotWidget = () => {
  const [index, setIndex] = useState(0);
  const [show, setShow] = useState(false);

  useEffect(() => {
    setTimeout(() => setShow(true), 1000);
  }, []);

  useEffect(() => {
    if (!show || index >= messages.length) return;
    const timeout = setTimeout(() => {
      setIndex((prev) => prev + 1);
    }, 2000);
    return () => clearTimeout(timeout);
  }, [index, show]);

  return (
    <div className="fixed bottom-6 right-6 z-50">
      <div className="bg-gray-900 text-white p-4 rounded-xl shadow-xl w-72 text-sm animate-fade-in">
        <div className="font-bold mb-2 text-cyan-400">ðŸ’¬ MindLoop Bot</div>
        <div className="space-y-2">
          {messages.slice(0, index).map((msg, i) => (
            <p key={i} className="bg-white/10 p-2 rounded">{msg}</p>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChatbotWidget;
