import React, { useEffect, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { MessageCircle } from "lucide-react";

const ChatbotWidget = () => {
  const { i18n } = useTranslation();
  const lang = i18n.language;
  const containerRef = useRef(null);
  const [visibleMessages, setVisibleMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [open, setOpen] = useState(true);
  const [input, setInput] = useState("");

  const initialConversations = {
    sr: [
      { from: "bot", text: "Zdravo! Ja sam tvoj asistent. ðŸ˜Š" },
    ],
    en: [
      { from: "bot", text: "Hello! I'm your assistant. ðŸ˜Š" },
    ],
    fr: [
      { from: "bot", text: "Salut ! Je suis votre assistant. ðŸ˜Š" },
    ],
    ru: [
      { from: "bot", text: "ÐŸÑ€Ð¸Ð²ÐµÑ‚! Ð¯ Ñ‚Ð²Ð¾Ð¹ Ð¿Ð¾Ð¼Ð¾Ñ‰Ð½Ð¸Ðº. ðŸ˜Š" },
    ],
  };

  const starterMessages = initialConversations[lang] || initialConversations["sr"];

  useEffect(() => {
    if (!open) return;

    setVisibleMessages(starterMessages);
    setIsTyping(false);
  }, [lang, open]);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [visibleMessages, isTyping]);

  const handleSend = () => {
    if (!input.trim()) return;
    const userMsg = { from: "user", text: input };
    setVisibleMessages((prev) => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);

    setTimeout(() => {
      setVisibleMessages((prev) => [
        ...prev,
        { from: "bot", text: `ðŸ§  ${lang === "sr" ? "Odgovor na:" : lang === "fr" ? "RÃ©ponse Ã  :" : lang === "ru" ? "ÐžÑ‚Ð²ÐµÑ‚ Ð½Ð°:" : "Response to:"} "${userMsg.text}"` },
      ]);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") handleSend();
  };

  if (!open) {
    return (
      <div
        className="fixed bottom-6 right-6 z-50 cursor-pointer animate-pulse"
        onClick={() => setOpen(true)}
      >
        <div className="bg-cyan-600 hover:bg-cyan-700 p-4 rounded-full shadow-lg text-white">
          <MessageCircle size={24} />
        </div>
      </div>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 z-50 font-sans">
      <div className="bg-gray-900 text-white p-4 rounded-xl shadow-xl w-80 text-sm relative">
        {/* Close button */}
        <button
          onClick={() => setOpen(false)}
          className="absolute top-2 right-2 text-gray-400 hover:text-white"
        >
          âœ–
        </button>

        <div className="font-bold mb-2 text-cyan-400">ðŸ’¬ MindLoop Bot</div>
        <div
          ref={containerRef}
          className="space-y-2 max-h-64 overflow-y-auto scroll-smooth pr-1 mb-3"
        >
          {visibleMessages.map((msg, i) => (
            <div
              key={i}
              className={`flex ${
                msg.from === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`rounded-lg px-3 py-2 max-w-[70%] ${
                  msg.from === "user"
                    ? "bg-white text-black"
                    : "bg-cyan-600 text-white"
                }`}
              >
                {msg.text}
              </div>
            </div>
          ))}

          {isTyping && (
            <div className="flex justify-start">
              <div className="bg-white/10 text-white px-3 py-2 rounded-lg animate-pulse">
                <span className="opacity-80">...</span>
              </div>
            </div>
          )}
        </div>

        {/* Input field */}
        <div className="flex items-center gap-2">
          <input
            type="text"
            className="flex-1 rounded-md px-3 py-1 bg-gray-100 text-black focus:outline-none text-sm"
            placeholder={lang === "sr" ? "Unesite poruku..." : lang === "fr" ? "Entrez un message..." : lang === "ru" ? "Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ..." : "Enter a message..."}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <button
            onClick={handleSend}
            className="bg-cyan-500 hover:bg-cyan-600 text-white px-3 py-1 rounded-md"
          >
            âž¤
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatbotWidget;
