import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    fallbackLng: 'sr',
    debug: false,
    interpolation: {
      escapeValue: false,
    },
    resources: {
      sr: { translation: {/* ... sadržaj kao gore ... */} },
      en: { translation: {/* ... sadržaj kao gore ... */} },
      fr: { translation: {/* ... sadržaj kao gore ... */} },
      ru: { translation: {/* ... sadržaj kao gore ... */} },
    },
  });

export default i18n;
