import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import ChatbotWidget from "../components/ChatbotWidget";
import FadeInSection from "../components/FadeInSection";

const Landing = () => {
  const { t, i18n } = useTranslation();
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(false);
    const timeout = setTimeout(() => setAnimate(true), 200);
    return () => clearTimeout(timeout);
  }, [i18n.language]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-800 text-white px-6 pt-20 pb-16 flex flex-col items-center">
      {/* TOP NAVBAR */}
      <div className="fixed top-0 left-0 w-full bg-black/70 backdrop-blur-lg border-b border-white/10 z-50 px-6 py-3 flex justify-between items-center">
        <div className="text-cyan-400 font-bold text-lg">MindLoop AI</div>
        <div className="flex items-center space-x-6 text-sm text-white">
          <a href="#kontakt" className="hover:text-cyan-400 transition">Kontakt</a>
          <select
            className="bg-gray-800 border border-white/20 px-2 py-1 rounded text-white text-sm"
            defaultValue={i18n.language}
            onChange={(e) => i18n.changeLanguage(e.target.value)}
          >
            <option value="sr">üá∑üá∏ SR</option>
            <option value="en">üá¨üáß EN</option>
            <option value="fr">üá´üá∑ FR</option>
            <option value="ru">üá∑üá∫ RU</option>
          </select>
        </div>
      </div>

      {/* HERO */}
      <FadeInSection>
        <div className="backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-8 shadow-lg max-w-2xl text-center mt-8">
          <h1 className="text-4xl font-bold mb-4 text-cyan-400 drop-shadow">{t("title")}</h1>
          <p className="text-lg mb-6 text-gray-300">{t("subtitle")}</p>
          <a href="https://mindloopaidemo.streamlit.app/" target="_blank" rel="noopener noreferrer">
            <button className="bg-cyan-500 hover:bg-cyan-600 text-white text-lg px-6 py-3 rounded-lg shadow-xl transition">
              {t("tryDemo")}
            </button>
          </a>
          <p className="text-sm text-gray-400 mt-2">100% {t("free") || "besplatno"}</p>
        </div>
      </FadeInSection>

      {/* BENEFITS */}
      <FadeInSection>
        <div className="mt-20 grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl w-full">
          {["instant", "smart", "easy"].map((key, i) => (
            <div
              key={i}
              className="backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-6 text-center shadow-md"
            >
              <div className="text-4xl mb-2">{key === "instant" ? "‚ö°" : key === "smart" ? "üß†" : "üõ†"}</div>
              <h3 className="text-xl font-semibold mb-1 text-cyan-300">{t(`benefits.${key}`)}</h3>
              <p className="text-gray-300 text-sm">{t(`benefits.${key}Desc`)}</p>
            </div>
          ))}
        </div>
      </FadeInSection>

      {/* FEATURES + AI DESCRIPTION */}
      <FadeInSection>
        <div className="mt-20 grid grid-cols-1 md:grid-cols-2 gap-8 max-w-6xl w-full items-center">
          <div className="bg-white/5 backdrop-blur-md border border-white/10 rounded-xl p-6 shadow-md">
            <h3 className="text-xl font-bold mb-4 text-cyan-400">{t("featuresTitle")}</h3>
            <ul className="space-y-3 text-white text-sm">
              {t("features", { returnObjects: true }).map((item, i) => (
                <li key={i} className="flex items-start">
                  <span className="text-green-400 mr-2">‚úî</span> {item}
                </li>
              ))}
            </ul>
          </div>
          <div className="bg-white/5 backdrop-blur-md border border-white/10 rounded-xl p-6 shadow-md text-white text-sm leading-relaxed">
            <h3 className="text-xl font-bold mb-4 text-cyan-400">{t("aiAssistantTitle")}</h3>
            <p className="mb-2">{t("aiAssistant1")}</p>
            <p className="mb-2">{t("aiAssistant2")}</p>
            <p className="text-green-400 mt-2">{t("aiAssistant3")}</p>
          </div>
        </div>
      </FadeInSection>

      {/* FOR WHOM */}
      <FadeInSection>
        <div className="mt-20 max-w-4xl text-center backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-8 shadow-lg">
          <h2 className="text-2xl font-bold mb-4 text-cyan-400">{t("forWhom")}</h2>
          <p className="text-gray-300 text-lg mb-6">{t("sectors")}</p>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 text-white font-medium text-sm">
            <div>üè® Turizam</div>
            <div>üöó Rent-a-car</div>
            <div>üè´ Edukacija</div>
            <div>üõç E-commerce</div>
            <div>üì¶ Dostava</div>
            <div>üì± IT servisi</div>
          </div>
        </div>
      </FadeInSection>

      {/* STATS */}
      <FadeInSection>
        <div className="mt-20 grid grid-cols-2 sm:grid-cols-4 gap-4 text-center text-sm text-white max-w-5xl">
          {[
            { key: "fast", icon: "‚è±" },
            { key: "users", icon: "üí¨" },
            { key: "uptime", icon: "üü¢" },
            { key: "langs", icon: "üåê" },
          ].map((stat, idx) => (
            <div key={idx} className="bg-white/5 p-4 rounded-lg">
              <p className="text-lg font-bold text-cyan-400">{t(`stats.${stat.key}`)}</p>
              <p className="text-gray-400">{stat.icon}</p>
            </div>
          ))}
        </div>
      </FadeInSection>

      {/* FAQ */}
      <FadeInSection>
        <div className="mt-20 max-w-4xl w-full">
          <h2 className="text-xl font-bold text-cyan-400 mb-6 text-center">{t("faqTitle")}</h2>
          <div className="space-y-4">
            {t("faq", { returnObjects: true }).map((item, idx) => (
              <div key={idx} className="bg-white/5 p-4 rounded-lg border border-white/10">
                <p className="font-semibold text-white">‚ùì {item.q}</p>
                <p className="text-gray-300 mt-1">üí° {item.a}</p>
              </div>
            ))}
          </div>
        </div>
      </FadeInSection>

      {/* FOOTER */}
      <footer id="kontakt" className="mt-24 text-center text-sm text-gray-500">
        <p>
          {t("footerEmail")}: <a href="mailto:mindloop.ai.contact@gmail.com" className="underline">mindloop.ai.contact@gmail.com</a>
        </p>
        <p>{t("footerPhone")}: +30 694 831 3741</p>
        <p className="mt-4">¬© {new Date().getFullYear()} MindLoop AI. All rights reserved.</p>
      </footer>

      {/* Chatbot u uglu */}
      <ChatbotWidget />
    </div>
  );
};

export default Landing;
