// src/pages/Landing.jsx
import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import ChatbotWidget from "../components/ChatbotWidget";

const Landing = () => {
  const { t, i18n } = useTranslation();
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(false);
    const timeout = setTimeout(() => setAnimate(true), 200);
    return () => clearTimeout(timeout);
  }, [i18n.language]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-800 text-white px-6 pt-20 pb-16 flex flex-col items-center">
      {/* TOP NAVBAR */}
      <div className="fixed top-0 left-0 w-full bg-black/70 backdrop-blur-lg border-b border-white/10 z-50 px-6 py-3 flex justify-between items-center">
        <div className="text-cyan-400 font-bold text-lg">MindLoop AI</div>
        <div className="flex items-center space-x-6 text-sm text-white">
          <a href="#kontakt" className="hover:text-cyan-400 transition">{t("navKontakt")}</a>
          <select
            className="bg-gray-800 border border-white/20 px-2 py-1 rounded text-white text-sm"
            defaultValue={i18n.language}
            onChange={(e) => {
              i18n.changeLanguage(e.target.value);
              setAnimate(false);
              setTimeout(() => setAnimate(true), 100); // soft update
            }}
          >
            <option value="sr">🇷🇸 SR</option>
            <option value="en">🇬🇧 EN</option>
            <option value="fr">🇫🇷 FR</option>
            <option value="ru">🇷🇺 RU</option>
          </select>
        </div>
      </div>

      {/* HERO */}
      <div className={`transform transition-all duration-700 ${
        animate ? "opacity-100 translate-y-0" : "opacity-0 translate-y-4"
      } backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-8 shadow-lg max-w-2xl text-center`}>
        <h1 className="text-4xl font-bold mb-4 text-cyan-400 drop-shadow">{t("title")}</h1>
        <p className="text-lg mb-6 text-gray-300">{t("subtitle")}</p>
        <a
          href="https://mindloopaidemo.streamlit.app/"
          target="_blank"
          rel="noopener noreferrer"
        >
          <button className="bg-cyan-500 hover:bg-cyan-600 text-white text-lg px-6 py-3 rounded-lg shadow-xl transition">
            {t("tryDemo")}
          </button>
        </a>
        <p className="text-sm text-gray-400 mt-2">100% {t("free") || "besplatno"}</p>
      </div>

      {/* BENEFITS */}
      <div className={`mt-20 grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl w-full transform transition-all duration-700 ${
        animate ? "opacity-100 translate-y-0" : "opacity-0 translate-y-4"
      }`}>
        {["instant", "smart", "easy"].map((key, i) => (
          <div
            key={i}
            className="backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-6 text-center shadow-md"
          >
            <div className="text-4xl mb-2">{key === "instant" ? "⚡" : key === "smart" ? "🧠" : "🛠"}</div>
            <h3 className="text-xl font-semibold mb-1 text-cyan-300">{t(`benefits.${key}`)}</h3>
            <p className="text-gray-300 text-sm">{t(`benefits.${key}Desc`)}</p>
          </div>
        ))}
      </div>

      {/* FEATURES + AI DESCRIPTION */}
      <div className="mt-20 grid grid-cols-1 md:grid-cols-2 gap-8 max-w-6xl w-full items-center">
        <div className="bg-white/5 backdrop-blur-md border border-white/10 rounded-xl p-6 shadow-md">
          <h3 className="text-xl font-bold mb-4 text-cyan-400">{t("featuresTitle")}</h3>
          <ul className="space-y-3 text-white text-sm">
            {t("features", { returnObjects: true }).map((item, i) => (
              <li key={i} className="flex items-start">
                <span className="text-green-400 mr-2">✔</span> {item}
              </li>
            ))}
          </ul>
        </div>

        <div className="bg-white/5 backdrop-blur-md border border-white/10 rounded-xl p-6 shadow-md text-white text-sm leading-relaxed">
          <h3 className="text-xl font-bold mb-4 text-cyan-400">{t("aiAssistantTitle")}</h3>
          <p className="mb-2">{t("aiAssistant1")}</p>
          <p className="mb-2">{t("aiAssistant2")}</p>
          <p className="text-green-400 mt-2">{t("aiAssistant3")}</p>
        </div>
      </div>

      {/* FOR WHOM */}
      <div className="mt-20 max-w-4xl text-center backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-8 shadow-lg transform transition-all duration-700">
        <h2 className="text-2xl font-bold mb-4 text-cyan-400">{t("forWhom")}</h2>
        <p className="text-gray-300 text-lg mb-6">{t("sectors")}</p>
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 text-white font-medium text-sm">
          {t("sectorsList", { returnObjects: true }).map((sector, i) => (
            <div key={i}>✅ {sector}</div>
          ))}
        </div>
      </div>

      {/* CONTACT SECTION */}
      <div id="kontakt" className="mt-24 max-w-4xl w-full text-center">
        <div className="backdrop-blur-md bg-white/5 border border-white/10 rounded-xl p-8 shadow-md">
          <h2 className="text-2xl font-bold text-cyan-400 mb-2">📬 {t("contactTitle") || "Kontaktirajte nas"}</h2>
          <p className="text-gray-300 mb-4 text-sm">
            Imate pitanja ili želite saradnju? Naš tim vam stoji na raspolaganju za brzu podršku i konsultacije.
          </p>
          <div className="flex flex-col sm:flex-row justify-center items-center gap-6 text-sm text-white">
            <a href="mailto:mindloop.ai.contact@gmail.com" className="hover:text-cyan-400 transition">
              ✉️ Email: mindloop.ai.contact@gmail.com
            </a>
            <a href="tel:+306948313741" className="hover:text-cyan-400 transition">
              📞 Telefon: +30 694 831 3741
            </a>
            <a href="https://www.linkedin.com/company/mindloop-ai" target="_blank" className="hover:text-cyan-400 transition">
              💼 LinkedIn
            </a>
          </div>
        </div>
        <p className="mt-6 text-gray-500 text-xs">© {new Date().getFullYear()} MindLoop AI. All rights reserved.</p>
      </div>

      {/* Chatbot Widget */}
      <ChatbotWidget />
    </div>
  );
};

export default Landing;
