import React from "react";
import { useTranslation } from "react-i18next";

const ChatbotWidget = () => {
  const { i18n } = useTranslation();
  const lang = i18n.language;

  const conversations = {
    sr: [
      { from: "bot", text: "Zdravo! Ja sam tvoj asistent. üòä" },
      { from: "user", text: "Kako da poruƒçim proizvod?" },
      { from: "bot", text: "Jednostavno! Kliknite na dugme 'Dodaj u korpu'." },
      { from: "user", text: "Koliko traje isporuka?" },
      { from: "bot", text: "U proseku 1 do 3 radna dana." },
    ],
    en: [
      { from: "bot", text: "Hello! I'm your assistant. üòä" },
      { from: "user", text: "How can I order a product?" },
      { from: "bot", text: "Simply click the 'Add to cart' button." },
      { from: "user", text: "How long does delivery take?" },
      { from: "bot", text: "On average, 1 to 3 business days." },
    ],
    fr: [
      { from: "bot", text: "Salut ! Je suis votre assistant. üòä" },
      { from: "user", text: "Comment commander un produit ?" },
      { from: "bot", text: "Cliquez simplement sur 'Ajouter au panier'." },
      { from: "user", text: "Combien de temps dure la livraison ?" },
      { from: "bot", text: "En moyenne, entre 1 et 3 jours ouvrables." },
    ],
    ru: [
      { from: "bot", text: "–ü—Ä–∏–≤–µ—Ç! –Ø —Ç–≤–æ–π –ø–æ–º–æ—â–Ω–∏–∫. üòä" },
      { from: "user", text: "–ö–∞–∫ –∑–∞–∫–∞–∑–∞—Ç—å —Ç–æ–≤–∞—Ä?" },
      { from: "bot", text: "–ü—Ä–æ—Å—Ç–æ –Ω–∞–∂–º–∏—Ç–µ –∫–Ω–æ–ø–∫—É '–î–æ–±–∞–≤–∏—Ç—å –≤ –∫–æ—Ä–∑–∏–Ω—É'." },
      { from: "user", text: "–°–∫–æ–ª—å–∫–æ –¥–ª–∏—Ç—Å—è –¥–æ—Å—Ç–∞–≤–∫–∞?" },
      { from: "bot", text: "–í —Å—Ä–µ–¥–Ω–µ–º –æ—Ç 1 –¥–æ 3 —Ä–∞–±–æ—á–∏—Ö –¥–Ω–µ–π." },
    ],
  };

  const chat = conversations[lang] || conversations["sr"];

  return (
    <div className="fixed bottom-6 right-6 z-50 font-sans">
      <div className="bg-gray-900 text-white p-4 rounded-xl shadow-xl w-80 text-sm">
        <div className="font-bold mb-2 text-cyan-400">üí¨ MindLoop Bot</div>
        <div className="space-y-2 max-h-80 overflow-y-auto">
          {chat.map((msg, i) => (
            <div
              key={i}
              className={`flex ${
                msg.from === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`rounded-lg px-3 py-2 max-w-[70%] ${
                  msg.from === "user"
                    ? "bg-cyan-600 text-white"
                    : "bg-white/10 text-white"
                }`}
              >
                {msg.text}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChatbotWidget;
