import React, { useState, useRef, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { MessageCircle } from "lucide-react";
import faqData from "./faqData";
import * as use from "@tensorflow-models/universal-sentence-encoder"; // koristi Universal Sentence Encoder (lagan, open-source)
import { cosineSimilarity } from "./cosine"; // vidi dole objaÅ¡njenje

import * as tf from '@tensorflow/tfjs';

// Obavezno ruÄno inicijalizuj backend:
tf.setBackend('webgl').then(() => {
  console.log('Backend set to WebGL');
});


const LandingChatbot = () => {
  const { t } = useTranslation();
  const [messages, setMessages] = useState([
    { sender: "bot", text: t("Hello! Ask me anything about AI Assistant or CopyBot, I can speak in English and Russian!") }
  ]);
  const [input, setInput] = useState("");
  const [open, setOpen] = useState(false);
  const [shake, setShake] = useState(false);
  const [typingDots, setTypingDots] = useState("");
  const containerRef = useRef(null);
  const [encoder, setEncoder] = useState(null);
  const [faqEmbeddings, setFaqEmbeddings] = useState([]);

  useEffect(() => {
    const loadModelAndEmbeddings = async () => {
      const model = await use.load();
      setEncoder(model);

      const embeddings = await Promise.all(
        faqData.map(faq => model.embed(faq.question).then(t => t.array()))
      );
      setFaqEmbeddings(embeddings.map(arr => arr[0]));
    };

    loadModelAndEmbeddings();
  }, []);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [messages]);

  const findBestAnswer = async (inputText) => {
    if (!encoder) return null;
  
    const inputEmbeddingTensor = await encoder.embed(inputText);
    const inputEmbedding = (await inputEmbeddingTensor.array())[0];
  
    let bestScore = -1;
    let bestAnswer = null;
  
    faqEmbeddings.forEach((faqEmbedding, index) => {
      const score = cosineSimilarity(inputEmbedding, faqEmbedding);
      if (score > bestScore) {
        bestScore = score;
        bestAnswer = faqData[index].answer;
      }
    });
  
    // OVDE: ako najbolji skor nije dovoljno dobar, vrati null
    if (bestScore < 0.45) {
      return null; // fallback
    }
  
    return bestAnswer;
  };
  

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);

    // Typing animation
    setMessages(prev => [...prev, { sender: "bot", text: "..." }]);
    setTypingDots(".");

    const typingInterval = setInterval(() => {
      setTypingDots(prev => (prev.length >= 3 ? "." : prev + "."));
    }, 400);

    const bestAnswer = await findBestAnswer(input);

    setTimeout(() => {
      clearInterval(typingInterval);
      setMessages(prev => {
        const newMessages = prev.filter((msg, idx) => !(idx === prev.length - 1 && msg.text === "..."));
        if (bestAnswer) {
          return [...newMessages, { sender: "bot", text: bestAnswer }];
        } else {
          return [...newMessages, { sender: "bot", text: t("I didnt understand, contact our team or ask me something else!") }];
        }
      });
      setShake(true);
      setTimeout(() => setShake(false), 600);
    }, 2000);

    setInput("");
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSend();
    }
  };

  if (!open) {
    return (
      <div className="fixed bottom-6 right-6 z-50 cursor-pointer animate-pulse" onClick={() => setOpen(true)}>
        <div className={`bg-cyan-600 hover:bg-cyan-700 p-4 rounded-full shadow-lg text-white ${shake ? "animate-shake" : ""}`}>
          <MessageCircle size={24} />
        </div>
      </div>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 z-50 font-sans">
      <div className="bg-gray-900 text-white p-4 rounded-xl shadow-xl w-80 text-sm relative">
        <button onClick={() => setOpen(false)} className="absolute top-2 right-2 text-gray-400 hover:text-white">
          âœ–
        </button>

        <div className="font-bold mb-2 text-cyan-400">ðŸ’¬ MindLoop AI</div>

        <div ref={containerRef} className="space-y-2 max-h-80 overflow-y-auto scroll-smooth pr-1">
          {messages.map((msg, i) => (
            <div key={i} className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
              <div className={`rounded-lg px-3 py-2 max-w-[70%] ${
                msg.sender === "user" ? "bg-cyan-600 text-white" : "bg-white/10 text-white"
              }`}>
                {msg.text === "..." ? typingDots : msg.text}
              </div>
            </div>
          ))}
        </div>

        <div className="flex items-center space-x-2 mt-4">
          <input
            type="text"
            className="flex-1 bg-gray-700 text-white rounded-xl px-4 py-2 text-sm outline-none"
            placeholder={t("inputPlaceholder")}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <button
            onClick={handleSend}
            className="bg-cyan-600 hover:bg-cyan-700 text-white px-3 py-2 rounded-xl text-sm"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default LandingChatbot;
