// ChatbotWidget.jsx
import React, { useState } from "react";
import { useTranslation } from "react-i18next";

const ChatbotWidget = () => {
  const { t, i18n } = useTranslation();
  const [isOpen, setIsOpen] = useState(true);

  const messages = {
    sr: [
      { from: "bot", text: "Zdravo! Ja sam tvoj AI asistent." },
      { from: "user", text: "Mogu li da poruƒçim iPhone 16?" },
      { from: "bot", text: "Naravno! 'iPhone 16' je dodat u va≈°u korpu. ≈Ωelite li i AirPods uz to?" },
      { from: "user", text: "Da, dodaj i njih i zavr≈°i porud≈æbinu." },
      { from: "bot", text: "Sve je spremno! Porud≈æbina je naruƒçena na va≈°u unetu adresu." }
    ],
    en: [
      { from: "bot", text: "Hi! I'm your AI assistant." },
      { from: "user", text: "Can I order an iPhone 16?" },
      { from: "bot", text: "Of course! 'iPhone 16' has been added to your cart. Would you also like AirPods?" },
      { from: "user", text: "Yes, add them as well, and finish the purchase." },
      { from: "bot", text: "All set! The order has been placed to your entered address." }
    ],
    fr: [
      { from: "bot", text: "Bonjour ! Je suis votre assistant IA." },
      { from: "user", text: "Puis-je commander un iPhone 16 ?" },
      { from: "bot", text: "Bien s√ªr ! 'iPhone 16' a √©t√© ajout√© √† votre panier. Souhaitez-vous aussi des AirPods ?" },
      { from: "user", text: "Oui, ajoutez-les aussi et finalisez la commande." },
      { from: "bot", text: "C'est fait ! La commande a √©t√© envoy√©e √† votre adresse." }
    ],
    ru: [
      { from: "bot", text: "–ü—Ä–∏–≤–µ—Ç! –Ø —Ç–≤–æ–π AI –ø–æ–º–æ—â–Ω–∏–∫." },
      { from: "user", text: "–ú–æ–≥—É –ª–∏ —è –∑–∞–∫–∞–∑–∞—Ç—å iPhone 16?" },
      { from: "bot", text: "–ö–æ–Ω–µ—á–Ω–æ! 'iPhone 16' –¥–æ–±–∞–≤–ª–µ–Ω –≤ –≤–∞—à—É –∫–æ—Ä–∑–∏–Ω—É. –•–æ—Ç–∏—Ç–µ —Ç–∞–∫–∂–µ AirPods?" },
      { from: "user", text: "–î–∞, –¥–æ–±–∞–≤—å –∏—Ö —Ç–æ–∂–µ –∏ –æ—Ñ–æ—Ä–º–∏ –∑–∞–∫–∞–∑." },
      { from: "bot", text: "–í—Å—ë –≥–æ—Ç–æ–≤–æ! –ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω –Ω–∞ –≤–∞—à –∞–¥—Ä–µ—Å." }
    ]
  };

  const currentMessages = messages[i18n.language] || messages.en;

  return (
    <div className="fixed bottom-4 right-4 z-50">
      {isOpen ? (
        <div className="w-80 bg-white/10 backdrop-blur-md border border-white/20 text-white rounded-xl shadow-xl p-4">
          <div className="flex justify-between items-center mb-2">
            <h4 className="text-cyan-400 font-semibold">MindLoop AI</h4>
            <button onClick={() => setIsOpen(false)} className="text-white hover:text-red-400 text-lg">‚úñ</button>
          </div>
          <div className="space-y-2 max-h-72 overflow-y-auto text-sm">
            {currentMessages.map((msg, idx) => (
              <div
                key={idx}
                className={`p-2 rounded-lg max-w-[85%] ${
                  msg.from === "bot" ? "bg-cyan-600 text-white self-start" : "bg-gray-200 text-black self-end ml-auto"
                }`}
              >
                {msg.text}
              </div>
            ))}
          </div>
        </div>
      ) : (
        <button
          onClick={() => setIsOpen(true)}
          className="bg-cyan-500 hover:bg-cyan-600 text-white px-4 py-2 rounded-full animate-pulse"
        >
          üí¨ ChatBot
        </button>
      )}
    </div>
  );
};

export default ChatbotWidget;
