import React, { useEffect, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { MessageCircle } from "lucide-react"; // Ako koristiÅ¡ lucide, ili zameni sa <svg> ili emoji

const ChatbotWidget = () => {
  const { i18n } = useTranslation();
  const lang = i18n.language;
  const containerRef = useRef(null);
  const [visibleMessages, setVisibleMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [open, setOpen] = useState(true);

  const conversations = {
    sr: [
      { from: "bot", text: "Zdravo! Ja sam tvoj asistent. ðŸ˜Š" },
      { from: "user", text: "Kako da poruÄim proizvod?" },
      { from: "bot", text: "Jednostavno! Kliknite na dugme 'Dodaj u korpu'." },
      { from: "user", text: "Koliko traje isporuka?" },
      { from: "bot", text: "U proseku 1 do 3 radna dana." },
    ],
    en: [
      { from: "bot", text: "Hello! I'm your assistant. ðŸ˜Š" },
      { from: "user", text: "How can I order a product?" },
      { from: "bot", text: "Simply click the 'Add to cart' button." },
      { from: "user", text: "How long does delivery take?" },
      { from: "bot", text: "On average, 1 to 3 business days." },
    ],
    fr: [
      { from: "bot", text: "Salut ! Je suis votre assistant. ðŸ˜Š" },
      { from: "user", text: "Comment commander un produit ?" },
      { from: "bot", text: "Cliquez simplement sur 'Ajouter au panier'." },
      { from: "user", text: "Combien de temps dure la livraison ?" },
      { from: "bot", text: "En moyenne, entre 1 et 3 jours ouvrables." },
    ],
    ru: [
      { from: "bot", text: "ÐŸÑ€Ð¸Ð²ÐµÑ‚! Ð¯ Ñ‚Ð²Ð¾Ð¹ Ð¿Ð¾Ð¼Ð¾Ñ‰Ð½Ð¸Ðº. ðŸ˜Š" },
      { from: "user", text: "ÐšÐ°Ðº Ð·Ð°ÐºÐ°Ð·Ð°Ñ‚ÑŒ Ñ‚Ð¾Ð²Ð°Ñ€?" },
      { from: "bot", text: "ÐŸÑ€Ð¾ÑÑ‚Ð¾ Ð½Ð°Ð¶Ð¼Ð¸Ñ‚Ðµ ÐºÐ½Ð¾Ð¿ÐºÑƒ 'Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð² ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñƒ'." },
      { from: "user", text: "Ð¡ÐºÐ¾Ð»ÑŒÐºÐ¾ Ð´Ð»Ð¸Ñ‚ÑÑ Ð´Ð¾ÑÑ‚Ð°Ð²ÐºÐ°?" },
      { from: "bot", text: "Ð’ ÑÑ€ÐµÐ´Ð½ÐµÐ¼ Ð¾Ñ‚ 1 Ð´Ð¾ 3 Ñ€Ð°Ð±Ð¾Ñ‡Ð¸Ñ… Ð´Ð½ÐµÐ¹." },
    ],
  };

  const allMessages = conversations[lang] || conversations["sr"];

  useEffect(() => {
    if (!open) return;

    setVisibleMessages([]);
    setIsTyping(false);
    let i = 0;

    const interval = setInterval(() => {
      const msg = allMessages[i];
      if (!msg) {
        clearInterval(interval);
        return;
      }

      if (msg.from === "bot") {
        setIsTyping(true);
        setTimeout(() => {
          setVisibleMessages((prev) => [...prev, msg]);
          setIsTyping(false);
        }, 1000);
      } else {
        setVisibleMessages((prev) => [...prev, msg]);
      }

      i++;
    }, 1800);

    return () => clearInterval(interval);
  }, [lang, open]);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [visibleMessages, isTyping]);

  if (!open) {
    return (
      <div
        className="fixed bottom-6 right-6 z-50 cursor-pointer animate-pulse"
        onClick={() => setOpen(true)}
      >
        <div className="bg-cyan-600 hover:bg-cyan-700 p-4 rounded-full shadow-lg text-white">
          <MessageCircle size={24} />
        </div>
      </div>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 z-50 font-sans">
      <div className="bg-gray-900 text-white p-4 rounded-xl shadow-xl w-80 text-sm relative">
        {/* Close button */}
        <button
          onClick={() => setOpen(false)}
          className="absolute top-2 right-2 text-gray-400 hover:text-white"
        >
          âœ–
        </button>

        <div className="font-bold mb-2 text-cyan-400">ðŸ’¬ MindLoop Bot</div>
        <div
          ref={containerRef}
          className="space-y-2 max-h-80 overflow-y-auto scroll-smooth pr-1"
        >
          {visibleMessages.map((msg, i) => (
            <div
              key={i}
              className={`flex ${
                msg.from === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`rounded-lg px-3 py-2 max-w-[70%] ${
                  msg.from === "user"
                    ? "bg-cyan-600 text-white"
                    : "bg-white/10 text-white"
                }`}
              >
                {msg.text}
              </div>
            </div>
          ))}

          {isTyping && (
            <div className="flex justify-start">
              <div className="bg-white/10 text-white px-3 py-2 rounded-lg animate-pulse">
                <span className="opacity-80">...</span>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatbotWidget;
