import React, { useState, useRef, useEffect } from "react";
import { MessageCircle } from "lucide-react";

const faqData = [
  { question: "what is copybot", answer: "CopyBot automatically copies and posts your content from VK to Telegram, saving you time and boosting engagement." },
  { question: "how to try copybot", answer: "You can try CopyBot for free for one week. Just contact us via @vkcopybot on Telegram." },
  { question: "how copybot works", answer: "CopyBot monitors your VK community and automatically reposts to your Telegram channels." },
  { question: "is copybot free", answer: "CopyBot offers a free trial for one week. After that, affordable plans are available." },
  { question: "what content types does copybot support", answer: "CopyBot supports text, images, videos, polls, and clips â€“ in any combination!" },
  { question: "can i control copybot", answer: "Yes, you have full control. You can pause autoposting anytime or add new channels." },
  { question: "hello", answer: "Hi there! ðŸ‘‹ Feel free to ask anything about CopyBot or our Chat Widget!" },
  { question: "hi", answer: "Hello! ðŸ‘‹ I'm here to answer your questions about CopyBot and AI assistants." },
  { question: "how much does copybot cost", answer: "After the free trial, CopyBot offers affordable monthly and yearly plans. Contact us for details!" },
  { question: "do you support images", answer: "Yes! CopyBot can repost text, images, videos, polls, and clips seamlessly." },
  { question: "do you support polls", answer: "Absolutely, CopyBot supports polls along with text, photos, videos, and clips." },
  { question: "can i add multiple telegram channels", answer: "Yes, you can easily add multiple Telegram channels for reposting." },
  { question: "can i stop autoposting", answer: "Of course! You can pause or disable autoposting anytime from your dashboard." },
  { question: "how to install copybot", answer: "No installation is required! CopyBot is cloud-based and connects directly to your VK and Telegram accounts." },
  { question: "is copybot easy to use", answer: "Yes! CopyBot is designed to be super easy. No coding skills needed." },
  { question: "can i customize copybot", answer: "You can customize settings like which posts to copy, delays, and channels." },
  { question: "what is mindloop ai", answer: "MindLoop AI is the company behind CopyBot and our smart AI website chatbot." },
  { question: "do you offer customer support", answer: "Yes! Our team is ready to help you via email or Telegram." },
  { question: "how long is the free trial", answer: "The free trial lasts 7 full days, so you can test all features!" },
  { question: "is copybot safe", answer: "Yes, CopyBot uses secure APIs and encrypted connections to ensure your data is protected." },
  { question: "can i post manually", answer: "Yes, you can use CopyBot for both automatic and manual reposting." },
  { question: "what languages are supported", answer: "We currently support English, Serbian, Russian, and French!" },
  { question: "how to contact support", answer: "You can contact us at mindloop.ai.contact@gmail.com or @vkcopybot on Telegram." },
  { question: "can i schedule posts", answer: "Scheduled posting is coming soon! ðŸš€ Stay tuned for updates." },
  { question: "does copybot support clips", answer: "Yes, CopyBot can repost clips along with other media types." },
  { question: "is copybot available worldwide", answer: "Yes, CopyBot can be used from anywhere in the world!" },
  { question: "who made copybot", answer: "CopyBot was created by MindLoop AI Co., a team passionate about automation and AI." },
  { question: "what is a chatbot widget", answer: "A chatbot widget is a small assistant you add to your website to automate conversations and help visitors." },
  { question: "can i add your chatbot to my website", answer: "Yes! Our AI chatbot can be easily integrated into any website with just a few lines of code." },
  { question: "what does your chatbot do", answer: "Our chatbot answers customer questions instantly, provides recommendations, and improves engagement." },
  { question: "can i try the chatbot", answer: "Yes! Try our chatbot demo at mindloopaidemo.streamlit.app." },
  { question: "how to add chatbot", answer: "Adding our chatbot to your site is simple: we provide you the script and setup help if needed." },
  { question: "is the chatbot free", answer: "We offer a free version with limited features and premium plans for full capabilities." },
  { question: "is your chatbot multilingual", answer: "Yes! It supports English, Serbian, French, and Russian." },
  { question: "what platforms do you support", answer: "We support websites, e-commerce shops, and custom platforms via our API." },
  { question: "do i need coding skills", answer: "No! Our system is designed so that even non-tech users can set it up easily." },
  { question: "how fast is your chatbot", answer: "Our chatbot responds in less than a second to user questions." },
  { question: "can your chatbot recommend products", answer: "Yes! It can suggest products, services, or answer FAQs based on your business." },
  { question: "is it secure", answer: "Yes, we use industry-standard security and data protection methods." },
  { question: "can i customize responses", answer: "Yes! We can customize the chatbot responses based on your business type." },
  { question: "are there any hidden fees", answer: "No hidden fees! We believe in transparent pricing." },
  { question: "what happens after free trial", answer: "You can choose to continue with a paid plan or cancel anytime." },
  { question: "can i cancel anytime", answer: "Yes, there are no contracts. Cancel your plan anytime you want." },
  { question: "do you offer discounts", answer: "Yes! We offer discounts for yearly plans and early adopters." },
  { question: "do you work with agencies", answer: "Yes! We have special plans for agencies managing multiple clients." },
  { question: "where are your servers", answer: "Our servers are located in top data centers in Europe." },
  { question: "how to upgrade plan", answer: "You can easily upgrade from your dashboard or by contacting us." },
];

const LandingChatbot = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hi! ðŸ‘‹ Ask me anything about CopyBot." }
  ]);
  const [input, setInput] = useState("");
  const [open, setOpen] = useState(false);
  const [shake, setShake] = useState(false);
  const [typingDots, setTypingDots] = useState("");

  const containerRef = useRef(null);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);

    const lowerInput = input.toLowerCase();
    let bestMatch = null;
    let highestScore = 0;

    faqData.forEach((faq) => {
      const question = faq.question.toLowerCase();
      let score = 0;
      const inputWords = lowerInput.split(" ");
      const questionWords = question.split(" ");
      inputWords.forEach((word) => {
        if (questionWords.includes(word)) {
          score++;
        }
      });
      if (score > highestScore) {
        highestScore = score;
        bestMatch = faq;
      }
    });

    // Start typing animation
    setMessages(prev => [...prev, { sender: "bot", text: "..." }]);
    setTypingDots(".");

    const typingInterval = setInterval(() => {
      setTypingDots(prev => (prev.length >= 3 ? "." : prev + "."));
    }, 400);

    setTimeout(() => {
      clearInterval(typingInterval);
      setMessages(prev => {
        const newMessages = prev.filter((msg, idx) => !(idx === prev.length - 1 && msg.text === "..."));
        if (bestMatch && highestScore > 0) {
          return [...newMessages, { sender: "bot", text: bestMatch.answer }];
        } else {
          return [...newMessages, { sender: "bot", text: "Sorry, I didn't understand. Please try asking differently or contact us at @vkcopybot!" }];
        }
      });
      setShake(true);
      setTimeout(() => setShake(false), 600);
    }, 2000);

    setInput("");
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSend();
    }
  };

  if (!open) {
    return (
      <div
        className="fixed bottom-6 right-6 z-50 cursor-pointer animate-pulse"
        onClick={() => setOpen(true)}
      >
        <div className={`bg-cyan-600 hover:bg-cyan-700 p-4 rounded-full shadow-lg text-white ${shake ? "animate-shake" : ""}`}>
          <MessageCircle size={24} />
        </div>
      </div>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 z-50 font-sans">
      <div className="bg-gray-900 text-white p-4 rounded-xl shadow-xl w-80 text-sm relative">
        <button
          onClick={() => setOpen(false)}
          className="absolute top-2 right-2 text-gray-400 hover:text-white"
        >
          âœ–
        </button>

        <div className="font-bold mb-2 text-cyan-400">ðŸ’¬ MindLoop AI</div>

        <div ref={containerRef} className="space-y-2 max-h-80 overflow-y-auto scroll-smooth pr-1">
          {messages.map((msg, i) => (
            <div key={i} className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
              <div className={`rounded-lg px-3 py-2 max-w-[70%] ${
                msg.sender === "user" ? "bg-cyan-600 text-white" : "bg-white/10 text-white"
              }`}>
                {msg.text === "..." ? typingDots : msg.text}
              </div>
            </div>
          ))}
        </div>

        <div className="flex items-center space-x-2 mt-4">
          <input
            type="text"
            className="flex-1 bg-gray-700 text-white rounded-xl px-4 py-2 text-sm outline-none"
            placeholder="Ask me something..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <button
            onClick={handleSend}
            className="bg-cyan-600 hover:bg-cyan-700 text-white px-3 py-2 rounded-xl text-sm"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default LandingChatbot;
